import React, { useEffect, useState } from 'react';
import { useSearchParams } from 'react-router-dom';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Line } from 'react-chartjs-2';
import axios from 'axios';
import './Dashboard.css'
  
export const Dashboard = () => {
    ChartJS.register(
        CategoryScale,
        LinearScale,
        PointElement,
        LineElement,
        Title,
        Tooltip,
        Legend
    );
       

    const [searchParams, setSearchParams] = useSearchParams();
    const [labels, setLabels] = useState([]);
    const [data, setData] = useState([[], []]);


    const getTelemetry = () => {
        const options = {
            method: "GET",
            url: `http://localhost:5000/api/v1/device/${searchParams.get("id")}/telemetry?sort=1`,
            withCredentials: true
        }
        axios(options)
        .then(r => {
            setLabels(r.data.data.map(x => new Date(x.ts).toLocaleTimeString()));
            let temp = r.data.data.map(x => x.telemetry.temp);
            let hum = r.data.data.map(x => x.telemetry.hum);
            setData([temp, hum]);
        })
    }

    useEffect(() => {
        getTelemetry();
        const intervalId = setInterval(() => getTelemetry(), 3000);
        return () => clearInterval(intervalId)
    }, [])

    const options = {
        responsive: true,
        plugins: {
            legend: {
                position: 'top',
            },
            title: {
                display: true,
                text: 'Temp and Hum',
                font: {
                    size: 30
                }
            },
        },
        scales: {
            y: {
                grid: {
                    color: '#A7ADAF'
                },
                ticks: {
                    color: '#00000'
                }
            },
            x: {
                grid: {
                    color: '#A7ADAF'
                },
                ticks: {
                    color: '#00000'
                }
            }
        }
  
    };

    const dataToPlot = {
        labels,
        datasets: [
          {
            label: 'Temperature',
            data: data[0],
            borderColor: '#ED1233',
            backgroundColor: '#FFFFFF',
            lineTension: 0.3
          },
          {
            label: 'Humidity',
            data: data[1],
            borderColor: '#0218FD',
            backgroundColor: '#FFFFFF',
            lineTension: 0.3
          },
        ],
    };

    return (
        <div className="container chart-container">
            <Line options={options} data={dataToPlot} />
        </div>
    )
}
