import React, { useEffect, useState } from 'react';
import Cookies from 'js-cookie';
import axios from 'axios';
import './Device.css'

export const Device = () => {

    const [data, setData] = useState([]);
    const [order, setOrder] = useState({name: "asc", type: "asc", _id: "asc"})

    const getDevices = () => {
        const token = Cookies.get("token");
        const options = {
            method: "GET",
            url: "http://localhost:5000/api/v1/user/all_devices",
            headers: {'Authorization': 'Bearer '+ token}
        }
        axios(options)
        .then(r => setData(r.data.data));
    }

    useEffect(() => getDevices(), []);

    const sort = (colName) => {
        if (order[colName] === "dec") {
            const sortedData = [...data].sort((a, b) => 
                a[colName].toLowerCase() > b[colName].toLowerCase() ? 1 : -1
            );
            setData(sortedData);
            setOrder({...order, [colName]: "asc"});
        }
        if (order[colName] === "asc") {
            const sortedData = [...data].sort((a, b) => 
                a[colName].toLowerCase() < b[colName].toLowerCase() ? 1 : -1
            );
            setData(sortedData);
            setOrder({...order, [colName]: "dec"});
        }
    }

    return (
        <div className="container">
            <table className="table">
                <thead>
                    <tr>
                        <th onClick={() => sort("name")}>Device Name {order.name === "asc" ? <i class="fa-solid fa-arrow-up"></i>: <i class="fa-solid fa-arrow-down"></i>}</th>
                        <th onClick={() => sort("type")}>Device Type  {order.type === "asc" ? <i class="fa-solid fa-arrow-up"></i>: <i class="fa-solid fa-arrow-down"></i>}</th>
                        <th onClick={() => sort("_id")}>ID {order._id === "asc" ? <i class="fa-solid fa-arrow-up"></i>: <i class="fa-solid fa-arrow-down"></i>}</th>
                    </tr>
                </thead>
                <tbody>
                    { data.map((d) => (
                        <tr key={d._id}>
                            <td>{d.name}</td>
                            <td>{d.type}</td>
                            <td>{d._id}</td>
                        </tr>
                    )) }
                </tbody>
            </table>
        </div>
    
    )
}
