import React, { useEffect, useState } from 'react';
import { useSearchParams } from 'react-router-dom';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    TimeScale,
    TimeSeriesScale,
} from 'chart.js';
import { Line } from 'react-chartjs-2';
import "chartjs-adapter-moment";
import axios from 'axios';
import './Dashboard.css'
  
export const Dashboard = () => {
    ChartJS.register(
        CategoryScale,
        TimeScale,
        TimeSeriesScale,
        LinearScale,
        PointElement,
        LineElement,
        Title,
        Tooltip,
        Legend
    );
       

    const [searchParams, setSearchParams] = useSearchParams();
    const [labels, setLabels] = useState([]);
    const [data, setData] = useState([[], []]);


    const getTelemetry = () => {
        const options = {
            method: "GET",
            url: `http://localhost:5000/api/v1/device/${searchParams.get("id")}/telemetry?sort=1`,
            withCredentials: true
        }
        axios(options)
        .then(r => {
            setLabels(r.data.data.map(x => x.ts));
            let temp = r.data.data.map(x => ({ 'x': x.ts, 'y': x.telemetry.temp }));
            let hum = r.data.data.map(x => ({ 'x': x.ts, 'y': x.telemetry.hum }));
            setData([temp, hum]);
        })
    }

    useEffect(() => {
        getTelemetry();
        const intervalId = setInterval(() => getTelemetry(), 3000);
        return () => clearInterval(intervalId)
    }, [])

    const options = {
        responsive: true,
        plugins: {
            legend: {
                position: 'top',
            },
            title: {
                display: true,
                text: 'Temp and Hum',
                color: '#000000',
                font: {
                    size: 30
                }
            },
        },
        scales: {
            y: {
                grid: {
                    color: '#A7ADAF'
                },
                ticks: {
                    color: '#00000'
                }
            },
            x: {
                grid: {
                    color: '#A7ADAF'
                },
                ticks: {
                    color: '#00000'
                },
                type: 'time',
                time: {
                    unit: 'minute',
                    stepSize: 5,
                },
                min: Date.now() - 24 * 60 *60,
                max: Date.now()
            }
        }
  
    };

    const dataToPlot = {
        datasets: [
            {
                label: 'Temperature',
                data: data[0],
                borderColor: '#ED1233',
                backgroundColor: '#ED1233',
                lineTension: 0.3,
                pointRadius: 5,
                pointHoverRadius: 10
            },
            {
                label: 'Humidity',
                data: data[1],
                borderColor: '#0218FD',
                backgroundColor: '#0218FD',
                lineTension: 0.3,
                pointRadius: 5,
                pointHoverRadius: 10
            },
        ],
    };

    return (
        <div className="container chart-container">
            <Line options={options} data={dataToPlot} />
        </div>
    )
}
